import React, { useEffect, useRef } from 'react'

const App1 = () => {
  const fname = useRef()
  const lname = useRef()

  const arr = JSON.parse(localStorage.getItem("detail")) || []

  useEffect(() => {
    setdata([...arr])
  }, [])
  return (
    <>
    <div>
      <input type='text' name='fname' ref={fname}/>
      <input type='text' name='lname' ref={lname}/>
      <button type='button'>Submit</button>
    </div>
    <div>
      <input type='text' name='fname' value={view.fname} />
      <input type='text' name='lname' value={view.lname} />
      <button type='button'>Update</button>
    </div>
    </>
  )
}

export default App1
