import React, { useEffect, useRef } from 'react'

const App1 = () => {
  const fname = useRef()
  const lname = useRef()

  const arr = JSON.parse(localStorage.getItem("detail")) || []

  useEffect(() => {
    setdata([...arr])
  }, [])

  const SubmitHandle = () => {
    const input = {
      fname : fname.current.value,
      lname : lname.current.value,
    }
    arr.push(input)
    localStorage.setItem("detail" , JSON.stringify(arr))
    setdata()
  }


  return (
    <>
    <div>
      <input type='text' name='fname' ref={fname}/>
      <input type='text' name='lname' ref={lname}/>
      <button type='button' onClick={SubmitHandle}>Submit</button>
    </div>
    <div>
      <input type='text' name='fname' value={view.fname} onChange={UpdateInputeHandler}/>
      <input type='text' name='lname' value={view.lname} onChange={UpdateInputeHandler}/>
      <button type='button' onClick={UpdateHandle}>Update</button>
    </div>
    </>
  )
}

export default App1
